//Add menu to admin
add_action('admin_menu', 'csm_menu');
function csm_menu() {
    add_menu_page('Coworking Space Manager', 'Coworking Space Manager', 'manage_options', 'coworking-space-manager', 'show_members', 'data:image/svg+xml;base64,', 2);
    add_submenu_page( 'coworking-space-manager', 'Members', 'Members', 'manage_options', 'coworking-space-manager');
    add_submenu_page( 'coworking-space-manager', 'Subscriptions', 'Subscriptions', 'manage_options', 'subscriptions', 'show_subscriptions');
}