app.factory('Services', function ($http, $timeout) {
    var factory = {};

    factory.membershipplans = function(base) {
        return $http.get('admin-ajax.php?action=csmmembershipplans').then(function (result) {
            return result.data;
        });
    };
    
    factory.members = function () {
        return $http.get('admin-ajax.php?action=csmmembers').then(function (result) {
            
            result.data.forEach(function(v) {
                if (v.first_name && v.last_name) {
                    v.display_name = v.last_name + ', ' + v.first_name;
                }
                else if (v.last_name) {
                    v.display_name = v.last_name;
                }
            });
            
            return result.data;
        });
    };

    return factory;
});
app.directive('invoice', function ($locale, $log, $document, $timeout, $uibModal, $filter) {
    return {
        bindToController: {
            invoiceSent: '=',
            invoiceSentAt: '@',
            paymentAt: '@',
            paymentMethod: '@',
            identifier: "@",
            price: "@",
            currencySymbol: "@",
            start: "@",
            end: "@",
            id: "@", 
            firstName: "@",
            lastName: "@",
            workplaceName: "@",
            planName: "@",
            days: "@",
            company: "@",
            address: "@",
            locality: "@",
            country: "@",
            csmName: "@",
            csmAddress: "@",
            csmZipcode: "@",
            csmLocality: "@",
            csmCountry: "@",
            csmEmail: "@",
            csmWebsite: "@"
        },
        controllerAs: 'vm',
        scope: {},
        controller: function ($scope) {
            var vm = this;
            console.log(vm);

            vm.openModal = function () {
                var modalInstance = $uibModal.open({
                    animation: true,
                    ariaLabelledBy: 'modal-title',
                    ariaDescribedBy: 'modal-body',
                    templateUrl: 'invoiceModalContent.html',
                    controller: function ($uibModalInstance, membership) {
                        var modal = this;
                        modal.membership = membership;
                        modal.membership.currentDate = $filter('date')(moment().format('YYYY-MM-DD'), 'mediumDate');
                        modal.membership.start = $filter('date')(modal.membership.start, 'mediumDate');
                        modal.membership.end = $filter('date')(modal.membership.end, 'mediumDate');
                        modal.membership.priceFormatted = $filter('currency')(modal.membership.price, modal.membership.currencySymbol);
                        
                        modal.ok = function () {
                            $uibModalInstance.close(modal.membership);
                        };

                        modal.cancel = function () {
                            $uibModalInstance.dismiss('cancel');
                        };
                    },
                    controllerAs: 'Ctrl',
                    windowClass: 'bootstrap-wrapper',
                    appendTo: angular.element($document[0].querySelector('#payment-modal')),
                    resolve: {
                        membership: function () {
                            return vm;
                        }
                    }
                });
                modalInstance.result.then(function () {
                    console.log('init');
                }, function () {
                    $log.info('Modal dismissed at: ' + new Date());
                });

                $timeout(function () {
                    $('#payment_at').datepicker({
                        language: 'en',
                        format: "yyyy-mm-dd",
                        todayHighlight: true,
                        autoclose: true
                    });
                    $('#payment_at').datepicker('setDate', new Date());
                });
            };

            return vm;
        },
        template: '<span ng-if="vm.invoiceSent"><i class="fa fa-fw fa-lg fa-check-circle text-success" aria-hidden="true"></i> <span ng-bind="vm.invoiceSentAt | date : \'mediumDate\'"></span></span>'
                + '<span ng-if="!vm.invoiceSent" class="btn btn-sm btn-default" ng-click="vm.openModal()"><i class="fa fa-fw fa-lg fa-exclamation-triangle text-warning" aria-hidden="true"></i> Send invoice</span>'
    };
});
app.directive('memberMembershipAdd', function ($locale, $timeout, Services) {
    return {
        bindToController: {
            ajaxBaseUrl: '@'
        },
        scope: true,
        controllerAs: 'vm',
        controller: function ($scope) {
            var vm = this;

            $scope.$watch('vm.ajaxBaseUrl', function (newVal, oldVal) {
                if (vm.ajaxBaseUrl) {
                    Services.membershipplans(vm.ajaxBaseUrl).then(function (res) {
                        vm.plans = res;

                        vm.selectedPlan = vm.plans[0];
                        vm.startDate = moment().format('YYYY-MM-DD');
                        vm.endDate = moment(vm.startDate).add(vm.selectedPlan.days, 'days').format();

                        vm.vatPercentages = [0, 6, 21];
                        vm.vat = vm.vatPercentages[0];

                        vm.selectedPlan.total_price = vm.selectedPlan.price + ((vm.selectedPlan.price / 100) * vm.vat);

                        $('#plan_start').datepicker({
                            language: 'en',
                            format: "yyyy-mm-dd",
                            startDate: vm.startDate,
                            todayHighlight: true,
                            autoclose: true
                        }).on('changeDate', function (e) {
                            $timeout(function () {
                                vm.startDate = moment(e.date).format('YYYY-MM-DD');
                                vm.endDate = moment(vm.startDate).add(vm.selectedPlan.days, 'days').format();
                                vm.selectedPlan.total_price = vm.selectedPlan.price + ((vm.selectedPlan.price / 100) * vm.vat);
                            });
                        });
                        $('#plan_start').datepicker('setDate', new Date());

                        $scope.$watch('vm.selectedPlan.days', function (newValue, oldValue) {
                            vm.endDate = moment(vm.startDate).add(vm.selectedPlan.days, 'days').format();
                            vm.selectedPlan.total_price = vm.selectedPlan.price + ((vm.selectedPlan.price / 100) * vm.vat);
                        });

                        $scope.$watch('vm.vat', function (newValue, oldValue) {
                            vm.selectedPlan.total_price = vm.selectedPlan.price + ((vm.selectedPlan.price / 100) * vm.vat);
                        });
                    });

                }
            });
            return vm;
        }
    };
});
app.directive('membershipAdd', function ($locale, $timeout, Services) {
    return {
        scope: true,
        controllerAs: 'vm',
        controller: function ($scope) {
            var vm = this;
            
            Services.members().then(function (res) {
                vm.members = res;
            });

            Services.membershipplans().then(function (res) {
                vm.plans = res;
                vm.selectedPlan = vm.plans[0];
                vm.startDate = moment().format('YYYY-MM-DD');
                vm.endDate = moment(vm.startDate).add(vm.selectedPlan.days, 'days').format();

                vm.vatPercentages = [0, 6, 21];
                vm.vat = vm.vatPercentages[0];

                vm.selectedPlan.total_price = vm.selectedPlan.price + ((vm.selectedPlan.price / 100) * vm.vat);

                $('#plan_start').datepicker({
                    language: 'en',
                    format: "yyyy-mm-dd",
                    startDate: vm.startDate,
                    todayHighlight: true,
                    autoclose: true
                }).on('changeDate', function (e) {
                    $timeout(function () {
                        vm.startDate = moment(e.date).format('YYYY-MM-DD');
                        vm.endDate = moment(vm.startDate).add(vm.selectedPlan.days, 'days').format();
                        vm.selectedPlan.total_price = vm.selectedPlan.price + ((vm.selectedPlan.price / 100) * vm.vat);
                    });
                });
                $('#plan_start').datepicker('setDate', new Date());

                $scope.$watch('vm.selectedPlan.days', function (newValue, oldValue) {
                    vm.endDate = moment(vm.startDate).add(vm.selectedPlan.days, 'days').format();
                    vm.selectedPlan.total_price = vm.selectedPlan.price + ((vm.selectedPlan.price / 100) * vm.vat);
                });

                $scope.$watch('vm.vat', function (newValue, oldValue) {
                    vm.selectedPlan.total_price = vm.selectedPlan.price + ((vm.selectedPlan.price / 100) * vm.vat);
                });

                return vm;
            });
        }
    };
});
